sources.styles/abstracts/_variables.scss:
@use 'layout' as *;
@use 'font-sizes' as *;
@use 'mixins' as *;
@use 'colors' as *;

:root {
   --ff-pm: 'DM Sans', sans-serif;
   --btn-transition: 0.2s linear;
   --nav-links-transition: 0.15s linear;
   --mobile-nav-transition: 0.3s ease-in-out;

   @each $color, $value in $colors {
      --clr-#{$color}: #{$value};
   }

   @each $screen-size, $map in $type-scale {
      @if $screen-size == mobile {
         @each $feature, $nested-map in $map {
            @each $prop, $value in $nested-map {
               --#{$prop}: #{$value};
            }
         }
      } @else if $screen-size == tablet {
         @include mq(min-width, tablet) {
            @each $feature, $nested-map in $map {
               @each $prop, $value in $nested-map {
                  --#{$prop}: #{$value};
               }
            }
         }
      } @else if $screen-size == desktop {
         @include mq(min-width, desktop) {
            @each $feature, $nested-map in $map {
               @each $prop, $value in $nested-map {
                  --#{$prop}: #{$value};
               }
            }
         }
      }
   }

   @each $screen-size, $map in $layout {
      @if $screen-size == mobile {
         @each $prop, $value in $map {
            --#{$prop}: #{$value};
         }
      } @else if $screen-size == tablet {
         @include mq(min-width, tablet) {
            @each $prop, $value in $map {
               --#{$prop}: #{$value};
            }
         }
      } @else if $screen-size == desktop {
         @include mq(min-width, desktop) {
            @each $prop, $value in $map {
               --#{$prop}: #{$value};
            }
         }
      }
   }
}

sources.styles/abstracts/_mixins.scss:
@use 'breakpoints' as *;

// =========================================
// BEM
// =========================================

@mixin e($element) {
   &__#{$element} {
      @content;
   }
}

@mixin m($modifier) {
   &--#{$modifier} {
      @content;
   }
}

// =========================================
// flex
// =========================================

@mixin flexy($disp: flex, $dir: null, $wrap: null, $just: null, $align: null) {
   display: $disp;
   flex-direction: $dir;
   flex-wrap: $wrap;
   justify-content: $just;
   align-items: $align;
}

// =========================================
// position
// =========================================

@mixin position($pos, $width: null, $height: null, $z-index: null) {
   position: $pos;
   width: $width;
   height: $height;
   z-index: $z-index;
   @content;
}

// =========================================
// bg-img
// =========================================

@mixin bg-img($img: null, $position: null, $size: null) {
   background-image: url('#{$img}');
   background-repeat: no-repeat;
   background-position: $position;
   background-size: $size;
}

// =========================================
// pseudo-el
// =========================================

@mixin p-el(
   $el,
   $content: '',
   $position: absolute,
   $width: null,
   $height: null
) {
   @if $el == 'before' or $el == 'after' {
      &::#{$el} {
         content: $content;
         position: $position;
         display: block;
         width: $width;
         height: $height;
         @content;
      }
   } @else {
      @error 'Before or after string required for valid pseudo element.';
   }
}

// =========================================
// uppercase
// =========================================

@mixin uc() {
   text-transform: uppercase;
   letter-spacing: 1px;
   // easier to read when applied  to non-headings
}

// =========================================
// media query
// =========================================

@mixin mq($width, $key) {
   @if $width ==
      'max-width' or
      $width ==
      'min-width' or
      $key ==
      'tablet' or
      $key ==
      'desktop'
   {
      @media only screen and ($width: map-get($breakpoints, $key)) {
         @content;
      }
   } @else {
      @error 'Tablet, medium, desktop & valid width required for media query.';
   }
}

@mixin mqs($minWidth, $minKey, $maxWidth, $maxKey) {
   @media only screen and ($minWidth: map-get($breakpoints, $minKey)) and ($maxWidth: map-get($breakpoints, $maxKey)) {
      @content;
   }
}

// =========================================
// feature queries
// =========================================

@mixin fq($prop, $val) {
   @supports (#{$prop}: $val) {
      @content;
   }
}

@mixin fqs($selector) {
   @supports selector(#{$selector}) {
      @content;
   }
}

