sources.styles/base/_reset.scss:
/* Box sizing rules */
*,
*::before,
*::after {
   box-sizing: border-box;
}

/* Remove default margin */
body,
h1,
h2,
h3,
h4,
p,
figure,
blockquote,
dl,
dd {
   margin: 0;
}

/*  permission to use hard wraps when no soft wrap opportunties */

p,
h1,
h2,
h3,
h4,
h5,
h6 {
   overflow-wrap: break-word;
}

/* Remove list styles on ul, ol elements with a list role, which suggests default styling will be removed */
ul[role='list'],
ol[role='list'] {
   list-style: none;
   padding: 0;
   margin: 0;
}

/* Set core root defaults */
html:focus-within {
   scroll-behavior: smooth;
}

/* Set core body defaults */
body {
   min-height: 100vh;
   text-rendering: optimizeSpeed;
   line-height: 1.5;
   overflow-x: hidden;
   -webkit-font-smoothing: antialiased;
}

/* A elements that don't have a class get default styles */
a:not([class]) {
   text-decoration-skip-ink: auto;
}

/* Make images easier to work with */

img,
picture,
video,
canvas,
svg {
   display: block;
   max-width: 100%;
}

/* Inherit fonts for inputs and buttons */
input,
button,
textarea,
select {
   font: inherit;
}

/* Remove all animations and transitions for people that prefer not to see them */
@media (prefers-reduced-motion: reduce) {
   html:focus-within {
      scroll-behavior: auto;
   }
   *,
   *::before,
   *::after {
      animation-duration: 0.01ms !important;
      animation-iteration-count: 1 !important;
      transition-duration: 0.01ms !important;
      scroll-behavior: auto !important;
   }
}

sources.styles/components/_button.scss:
@use 'abstracts' as *;

.btn {
   display: inline-block;
   @include m(bg) {
      padding: 1em 2em;
      transition: background-color var(--btn-transition),
         color var(--btn-transition);

      @include m(dark) {
         &:hover {
            background-color: hsl(var(--clr-light-grey-btn));
            color: hsl(var(--clr-pure-black));
         }
      }

      @include m(light) {
         &:hover {
            background-color: hsl(var(--clr-pure-white) / 0.8);
            color: hsl(var(--clr-pure-black));
         }
      }
   }

   @include m(text) {
      position: relative;
      @include p-el(after) {
         right: -55px;
         top: 50%;
         transform: translateY(-40%);
      }
      &:hover {
         text-decoration: underline;
      }

      @include m(dark) {
         @include p-el(after, url('../assets/shared/desktop/arrow.svg'));
      }

      @include m(light) {
         @include p-el(after, url('../assets/shared/desktop/arrow-white.svg'));
      }

      @include m(story) {
         width: 100%;
         &::after {
            right: 0;
            transform: translateY(-50%);
         }
      }
   }
}

sources.styles/components/_acccent-bar.scss:
@use 'abstracts' as *;

.accent-bar {
   background-repeat: no-repeat;
   @include p-el(before, $width: 5px, $height: 100%) {
      @include position(absolute, $z-index: 2) {
         left: 0;
      }
      background-image: linear-gradient(
         to top,
         hsl(var(--clr-accent-orange)),
         hsl(var(--clr-accent-pink)),
         hsl(var(--clr-accent-blue))
      );

      @include mq(max-width, tablet) {
         width: 180px;
         height: 5px;
         left: 0;
         background-image: linear-gradient(
            to right,
            hsl(var(--clr-accent-orange)) 5%,
            hsl(var(--clr-accent-pink)) 13%,
            hsl(var(--clr-accent-blue)) 50%
         );
      }
   }

   @include m(horizontal) {
      @include p-el(before, $width: 100%, $height: 5px) {
         @include position(absolute, $z-index: 2) {
            bottom: 0;
            left: 0;
         }
         opacity: 0;
         background-image: linear-gradient(
            to right,
            hsl(var(--clr-accent-orange)) 5%,
            hsl(var(--clr-accent-pink)) 15%,
            hsl(var(--clr-accent-blue)) 40%
         );
         transition: opacity var(--btn-transition);
      }

      @include m(pricing) {
         @include p-el(before, $width: 100%, $height: 5px) {
            top: 0;
         }

         @include mq(min-width, tablet) {
            @include p-el(before, $width: 100%, $height: 5px) {
               opacity: 1;
               top: 0;
               background-image: linear-gradient(
                  to right,
                  hsl(var(--clr-accent-orange)) 5%,
                  hsl(var(--clr-accent-pink)) 15%,
                  hsl(var(--clr-accent-blue)) 40%
               );
            }
         }
      }
   }

   @include m(vertical) {
      @include mq(max-width, tablet) {
         @include p-el(before) {
            left: 3px;
            top: -80px;
         }
      }
      @include m(cta) {
         @include mq(max-width, tablet) {
            @include p-el(before) {
               top: 0px;
               left: 8%;
            }
            @include mq(min-width, tablet) {
               @include p-el(before) {
                  top: -30px;
               }
            }
         }
      }
   }
}

