sources.styles/layout/_nav.scss:
@use 'abstracts' as *;

// Mobile

.nav-mobile {
   @include m(container) {
      @include mq(max-width, tablet) {
         padding: 3rem 0 5.5rem;
         opacity: 0;
         pointer-events: none;
         @include position(fixed, $z-index: 9997, $width: 100%) {
            top: 65px;
            left: 0;
         }
         transition: opacity var(--mobile-nav-transition);
      }
      &[data-visible='true'] {
         opacity: 1;
         pointer-events: all;
      }
   }

   @include m(hr) {
      margin: 0.7rem auto 1.4rem;
      width: 90%;
      border: 0;
      height: 0;
      border-top: 1px solid hsl(var(--clr-light-grey-body) / 0.8);
      @include mq(min-width, tablet) {
         display: none;
      }
   }
}

// body

body {
   @include mq(max-width, tablet) {
      @include p-el(before) {
         @include position(fixed, $z-index: 9996) {
            pointer-events: none;
            inset: 20% 0 0 0;
            background-color: hsl(var(--clr-pure-black) / 0.6);
            opacity: 0;
            transition: opacity var(--mobile-nav-transition);
         }
      }

      &[data-visible='true'] {
         overflow-y: hidden;
         @include p-el(before) {
            opacity: 1;
            pointer-events: all;
         }
      }
   }
}

// general + wide

.nav {
   & a {
      display: block;
      transition: color var(--nav-links-transition);
      &:hover {
         color: hsl(var(--clr-light-grey-btn));
      }
      @include mq(max-width, tablet) {
         padding-block: 0.5rem;
         width: 100%;
      }
   }

   @include e(list) {
      @include mq(min-width, tablet) {
         @include flexy($dir: row);
         gap: 2.1rem;
      }
   }

   @include m(sd) {
      @include e(list) {
         & a:hover {
            color: hsl(var(--clr-light-grey-btn) / 0.3);
         }
         @include mq(min-width, desktop) {
            @include flexy($dir: column);
            gap: 1rem;
         }
      }
   }
}

