   }
}

sources.styles/layout/_footer.scss:
@use 'abstracts' as *;

.footer {
   @include m(pm) {
      padding: var(--ct-padding-footer);
   }
}

.grid-container {
   @include m(footer) {
      grid-template-areas:
         'logo'
         'social'
         'nav'
         'cta'
         'copyright';
      gap: 2rem;

      @include mq(max-width, tablet) {
         place-items: center;
      }

      @include mq(min-width, tablet) {
         grid-template-columns: auto 1fr auto;
         grid-template-rows: repeat(2, auto) 1fr auto;
         grid-template-areas:
            'logo . cta'
            'nav nav .'
            '. . .'
            'social . copyright';
      }

      @include mq(min-width, desktop) {
         gap: 0 7rem;
         grid-template-rows: auto 1fr auto;
         grid-template-areas:
            'logo nav cta'
            '. nav .'
            'social . copyright';
      }

      @include e(logo) {
         grid-area: logo;
      }

      @include e(social) {
         grid-area: social;
      }

      @include e(nav) {
         grid-area: nav;
         @include mq(max-width, tablet) {
            margin-bottom: 4rem;
         }
         @include mq(min-width, desktop) {
            grid-row: 1 / -1;
         }
      }

      @include e(cta) {
         grid-area: cta;
         @include mq(max-width, tablet) {
            margin-left: -3rem;
         }
         @include mq(min-width, tablet) {
            @include flexy($dir: column, $align: center);
         }
      }

      @include e(copyright) {
         grid-area: copyright;
      }
   }
}

sources.styles/layout/_cta.scss:
@use 'abstracts' as *;

.cta-beta {
   display: grid;
   grid-template-rows: minmax(200px, 1fr);
   position: relative;
   padding: var(--ct-padding-cta);
   @include bg-img(
      '../assets/shared/mobile/bg-beta.jpg',
      $position: center,
      $size: cover
   );

   @include mq(min-width, tablet) {
      @include bg-img('../assets/shared/tablet/bg-beta.jpg');
   }

   @include mq(min-width, desktop) {
      @include bg-img('../assets/shared/desktop/bg-beta.jpg');
   }

   @include e(content) {
      gap: 3rem;
      @include mq(min-width, tablet) {
         gap: 0rem;
         grid-template-columns: repeat(2, 1fr);
      }
   }

   @include e(title) {
      @include m(ct) {
         @include mq(min-width, tablet) {
            @include flexy($dir: column, $just: center);
         }
      }

      margin-bottom: 0;
      max-width: 400px;
   }

   @include e(link) {
      @include mq(min-width, tablet) {
         align-self: center;
         justify-self: end;
         margin-right: 4rem;
      }
   }
}

sources.styles/utilities/_container.scss:
@use 'abstracts' as *;

.container-wrapper {
   max-width: 1200px;
   margin-inline: auto;
   width: 85%;
   @include mq(min-width, tablet) {
      width: 90%;
   }
}

sources.styles/utilities/_sr-only.scss:
@use 'abstracts' as *;

.sr-only {
   position: absolute;
   width: 1px;
   height: 1px;
   padding: 0;
   margin: -1px;
   overflow: hidden;
   clip: rect(0, 0, 0, 0);
   white-space: nowrap;
   border: 0;
}

sources.styles/utilities/_grid.scss:
@use 'abstracts' as *;

.grid-container {
   display: grid;
   @include mq(max-width, tablet) {
      grid-template-columns: 1fr;
      grid-auto-rows: auto;
   }
}

sources.styles/pages/_pricing.scss:
@use 'abstracts' as *;

.pricing-plans {
   padding-top: 7rem;
   @include e(toggle-container) {
      @include flexy($align: center, $just: center);
      gap: 2rem;
      margin-bottom: 3.5rem;
   }

   @include e(grid-container) {
      gap: 1.5rem;
      @include mq(min-width, desktop) {
         grid-template-columns: repeat(3, 1fr);
         gap: 3rem;
      }
   }
}

// table

.compare {
   padding-block: 10rem;
   @include e(table) {
      border-collapse: collapse;
      margin-top: 3rem;
      font-size: 0.9rem;
      min-width: 400px;
      max-width: 800px;

      // mobile

      @include mq(max-width, tablet) {
         table,
         thead,
         tbody,
         th,
         td,
         tr {
            display: block;
         }

         .compare__table__header--heading {
            @include mq(max-width, tablet) {
               position: absolute;
               top: -9999px;
               left: -9999px;
            }
         }

         .compare__table__body--row {
            & > *:not(:first-child) {
               display: inline-block;
               margin-right: min(10rem, 23vw);
            }
         }
      }

      & th,
      & td {
         @include mq(max-width, tablet) {
            padding: 1.5rem 0;
         }
         padding: 1.5rem 2.5rem;
      }

      @include e(body) {
         & tr {
            border-bottom: 1px solid hsl(var(--clr-light-grey-body) / 0.2);
         }
         @include e(title) {
            text-transform: uppercase;
            letter-spacing: 2px;
            color: hsl(var(--clr-pure-black));
         }
      }

      @include e(header) {
         border-bottom: 1px solid hsl(var(--clr-pure-black));
         & tr {
            text-align: left;
            font-weight: bold;
            color: hsl(var(--clr-pure-black));
            text-transform: uppercase;
            letter-spacing: 2px;
         }
      }

      @include e(tick) {
         // outline: 1px solid red;
         @include mq(max-width, tablet) {
            position: relative;
            @include p-el(before, $content: 'basic') {
               display: block;
               position: absolute;
               top: 0;
               color: hsl(var(--clr-light-grey-body) / 0.7);
               @include uc();
               font-size: 0.7rem;
            }
            @include m(pro) {
               @include p-el(before, $content: 'pro');
            }
            @include m(business) {
               @include p-el(before, $content: 'business');
            }
         }
      }
   }
}
